mapper.select:
SELECT DISTINCT COUNT( DISTINCT vedio_id) totalPages FROM TB_VEDIO_PLAYER
vedio_name LIKE '%'+#{vedioName}+'%'
AND vedio_main_user LIKE '%'+#{vedioMainUser}+'%'
AND vedio_director LIKE '%'+#{vedioDirector}+'%'
AND is_valid = #{valid}
AND create_name = #{createName}

mapper.update:
UPDATE TB_VEDIO_PLAYER SET
is_valid = #{valid},
update_time = GETDATE() WHERE vedio_id = #{vedioId}
