mapper.select:
SELECT reptile_id AS reptileId ,reptile_title AS reptileTitle ,reptite_content AS reptileContent ,reptile_img_src AS pretileImgSrc ,reptile_time AS reptileTime ,create_time AS createTime FROM tb_reptile_news WHERE 1=1
AND reptile_title = #{reptileTitle}
AND reptile_time = #{reptileTime}
AND reptile_id = #{reptileId}

mapper.update:

    	UPDATE tb_reptile_news SET

    			is_valid = #{valid},

		 WHERE reptile_id = #{reptileId}
