mapper.select:
SELECT DISTINCT COUNT( DISTINCT music_id) totalPages FROM TB_MUSIC_PLAYER
music_name LIKE '%'+#{musicName}+'%'
AND music_singer LIKE '%'+#{musicSinger}+'%'
AND is_valid = #{valid}
AND create_name = #{createName}

SELECT music_id AS musicId, music_name AS musicName, file_name AS fileName, music_singer AS musicSinger, music_src AS musicSrc, create_name AS createName, is_valid AS valid, create_time AS createTime, update_time AS updateTime FROM TB_MUSIC_PLAYER
music_id = #{musicId}
AND music_src = #{musicSrc}

mapper.update:
UPDATE TB_MUSIC_PLAYER SET
is_valid = #{valid},
update_time = GETDATE() WHERE music_id = #{musicId}
