mapper.select:
SELECT DISTINCT COUNT( DISTINCT a.pre_file_id) totalPages FROM tb_pre_file a INNER JOIN tb_tv_list b ON a.pre_file_id = b.pre_file_id
a.pre_file_name LIKE '%'+#{preFileName}+'%'
AND a.is_valid = #{valid}
AND a.create_name = #{createName}

mapper.update:

UPDATE tb_pre_file SET 

		is_valid = #{valid}

WHERE pre_file_id = #{preFileId}
